package main

import (
	"fmt"
	"strings"
)

func findMarker(data string) int {
	characters := strings.Split(data, "")
	var marker []string
	var position int
	for i, v := range characters {
		position = i + 1
		if len(marker) < 14 {
			marker = append(marker, v)
			continue
		}
		marker = marker[1:]
		marker = append(marker, v)
		if checkOnlyUniqueChar(marker) {
			break
		}

	}
	return position
}

func checkOnlyUniqueChar(data []string) bool {
	text := strings.Join(data, "")
	for _, v := range data {
		times := strings.Count(text, v)
		if times > 1 {
			return false
		}
	}
	return true
}

func main() {
	given := ""

	marker := findMarker(given)

	fmt.Println(marker)

}
